void mycvt(double d, char *buf) {
    if (d < 0) {
        *buf++ = '-';
        d = -d;
    }

    if (!d) {
        *buf++ = '0';
        *buf++ = 0;
        return;
    }

    int pw = (int)log10(d);
    int e = 1;
    int beforeDot = 1;

    if (0.000001 <= d && d < 1e21) {
        if (pw > 0) {
            d /= p10(pw);
            beforeDot = 1 + pw;
        }
    } else {
        d /= p10(pw);
        e = pw;
    }

    int sig = 0;
    while (sig < 17 || beforeDot > 0) {
        // printf("%f sig=%d bd=%d\n", d, sig, beforeDot);
        int c = (int)d;
        *buf++ = '0' + c;
        d = (d - c) * 10;
        if (--beforeDot == 0)
            *buf++ = '.';
        if (sig || c)
            sig++;
    }

    buf--;
    while (*buf == '0')
        buf--;
    if (*buf == '.')
        buf--;
    buf++;

    if (e != 1) {
        *buf++ = 'e';
        itoa(e, buf);
    } else {
        *buf = 0;
    }
}

String toString(TValue v) {
    ValType t = valType(v);

    if (t == ValType::String) {
        return (String)(void *)incr(v);
    } else if (t == ValType::Number) {
        char buf[64];

        if (isNumber(v)) {
            itoa(numValue(v), buf);
            return mkString(buf);
        }

        double x = toDouble(v);

#ifdef PXT_BOX_DEBUG
        if (x == (int)x) {
            itoa((int)x, buf);
            return mkString(buf);
        }
#endif

        if (isnan(x))
            return (String)(void *)sNaN;
        if (isinf(x)) {
            if (x < 0)
                return (String)(void *)sMInf;
            else
                return (String)(void *)sInf;
        }
        mycvt(x, buf);

        return mkString(buf);
    } else if (t == ValType::Function) {
        return (String)(void *)sFunction;
    } else {
        if (v == TAG_UNDEFINED)
            return (String)(void *)sUndefined;
        else if (v == TAG_FALSE)
            return (String)(void *)sFalse;
        else if (v == TAG_TRUE)
            return (String)(void *)sTrue;
        else if (v == TAG_NULL)
            return (String)(void *)sNull;

        auto vt = getVTable((RefObject *)v);
        if (vt->methods[2]) {
            // custom toString() method
            // after running action, make sure it's actually a string
            return stringConv(runAction1((Action)vt->methods[2], v));
        }
        return (String)(void *)sObject;
    }
}

String stringConv(TValue v) {
    ValType t = valType(v);
    if (t == ValType::String) {
        return (String)v;
    } else {
        auto r = toString(v);
        decr(v);
        return r;
    }
}
